defaultQuery:
query Query {
    users(usernames: ["user1", "user3", "user4"]) {
      pageInfo {
        endCursor
        startCursor
        hasNextPage
      }
      nodes {
        id
        username
        publicEmail
        location
        webUrl
        userPermissions {
          createSnippet
        }
      }
    }
  }

query Query {
    launchesPast(limit: 10) {
      mission_name
      launch_date_local
      launch_site {
        site_name_long
      }
      links {
        article_link
        video_link
      }
      rocket {
        rocket_name
      }
    }
  }

query Characters {
  characters {
    results {
      name
      location {
        name
      }
      gender
    }
  }
}

query AllFilms {
  allFilms {
    edges {
      node {
        title
        releaseDate
      }
    }
  }
}

query Games {
  allGames {
    name
    players {
      id
    }
  }
}
